import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import '../css/ForumTopic.css';  // Import CSS

const socket = io('http://localhost:5000');

function ForumTopic() {
    const { id } = useParams();  // Get the topic ID from the URL
    const [topic, setTopic] = useState(null);
    const [posts, setPosts] = useState([]);  // Initialize posts as an empty array
    const [newPost, setNewPost] = useState('');
    const [replyPost, setReplyPost] = useState(null);  // To store the post ID we're replying to
    const [newReply, setNewReply] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTopicData = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/topics/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch topic data');
                }
                const data = await response.json();
                setTopic(data);
                setPosts(data.Posts || []);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching topic:', error);
                setLoading(false);
            }
        };

        fetchTopicData();

        socket.on('newPost', (post) => {
            setPosts((prevPosts) => [post, ...prevPosts]);
        });

        return () => {
            socket.off('newPost');
        };
    }, [id]);

    const handleSubmitPost = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');

        if (!token || isTokenExpired(token)) {
            // Replace alert with a dynamic message
            alert('Session expired. Please log in again.');
            // Redirect user to login page
            navigate('/login');
            return;
        }

        const response = await fetch(`http://localhost:5000/api/topics/${id}/posts`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ content: newPost })
        });

        if (response.ok) {
            setNewPost('');  // Clear the input after submission
        }
    };

    const handleReplySubmit = async (e) => {
        e.preventDefault();
        const token = localStorage.getItem('token');
        if (!token) {
            alert('You must be logged in to reply.');
            return;
        }

        const response = await fetch(`http://localhost:5000/api/topics/${id}/posts`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ content: newReply, parentId: replyPost })  // Parent ID for the reply
        });

        if (response.ok) {
            setReplyPost(null);
            setNewReply('');  // Clear the input after submission
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;
    }

    return (
        <div className="forum-topic">
            {topic && (
                <>
                    <div className="topic-header">
                        <h2>{topic.title}</h2>
                        <p>{topic.description}</p>
                    </div>

                    <ul className="posts-list">
                        {posts.map((post) => (
                            <li key={post.id} className="post-item">
                                <p>{post.content}</p>
                                <small>
                                    Posted by: {post.User ? post.User.username : 'Anonymous'} at {new Date(post.createdAt).toLocaleString()}
                                    <button
                                        className="reply-button"
                                        onClick={() => setReplyPost(post.id)}
                                    >
                                        Reply
                                    </button>
                                </small>

                                {/* Display replies */}
                                {post.Replies && post.Replies.length > 0 && (
                                    <ul className="replies-list">
                                        {post.Replies.map((reply) => (
                                            <li key={reply.id} className="reply-item">
                                                <p>
                                                    <strong>Reply to: {post.User ? post.User.username : 'Anonymous'}:</strong> {reply.content}
                                                </p>
                                                <small>
                                                    Replied by: {reply.User ? reply.User.username : 'Anonymous'} at {new Date(reply.createdAt).toLocaleString()}
                                                </small>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </li>
                        ))}
                    </ul>

                    {replyPost && (
                        <form onSubmit={handleReplySubmit} className="new-reply-form">
                            <textarea
                                placeholder="Write your reply..."
                                value={newReply}
                                onChange={(e) => setNewReply(e.target.value)}
                                required
                            />
                            <button type="submit">Reply</button>
                        </form>
                    )}

                    <form onSubmit={handleSubmitPost} className="new-post-form">
                        <textarea
                            placeholder="Write your post..."
                            value={newPost}
                            onChange={(e) => setNewPost(e.target.value)}
                            required
                        />
                        <button type="submit">Post</button>
                    </form>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
