import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import '../css/ForumTopic.css';

const socket = io('http://localhost:5000');  // Connect to Socket.IO

function ForumTopic() {
    const { id } = useParams();
    const [topic, setTopic] = useState(null);
    const [posts, setPosts] = useState([]);
    const [newPost, setNewPost] = useState('');
    const [replyPost, setReplyPost] = useState(null);  // Track the post being replied to
    const [newReply, setNewReply] = useState('');
    const [loading, setLoading] = useState(true);

    useEffect(() => {
        const fetchTopicData = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/topics/${id}`);
                if (!response.ok) throw new Error('Failed to fetch topic data');
                const data = await response.json();
                setTopic(data);
                setPosts(data.Posts || []);
                setLoading(false);
            } catch (error) {
                console.error('Error fetching topic:', error);
                setLoading(false);
            }
        };

        fetchTopicData();

        socket.on('newPost', (post) => {
            console.log('New post received:', post);  // Debugging log
            setPosts((prevPosts) => [post, ...prevPosts]);
        });

        return () => {
            socket.off('newPost');
        };
    }, [id]);

    // Debugging added to verify if this is being triggered
    const handleSubmitPost = async (e) => {
        e.preventDefault();
        console.log('Post submission triggered');  // Log to check if the post request is triggered
        const token = localStorage.getItem('token');
        if (!token) {
            alert('You must be logged in to post.');
            return;
        }

        const response = await fetch(`http://localhost:5000/api/topics/${id}/posts`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ content: newPost })
        });

        if (response.ok) {
            console.log('Post submitted successfully');  // Log for debugging
            setNewPost('');
        }
    };

    const handleReplySubmit = async (e) => {
        e.preventDefault();
        console.log('Submitting reply to post ID:', replyPost);  // Debugging to check if replyPost is being passed
        const token = localStorage.getItem('token');
        if (!token) {
            alert('You must be logged in to reply.');
            return;
        }

        const response = await fetch(`http://localhost:5000/api/topics/${id}/posts`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${token}`
            },
            body: JSON.stringify({ content: newReply, parentId: replyPost })
        });

        if (response.ok) {
            console.log('Reply submitted successfully');
            setReplyPost(null);
            setNewReply('');
        } else {
            console.log('Failed to submit reply');  // Debugging log if the reply fails
        }
    };


    if (loading) return <div className="loading">Loading...</div>;

    return (
        <div className="forum-topic">
            {topic && (
                <>
                    <div className="topic-header">
                        <h2>{topic.title}</h2>
                        <p>{topic.description}</p>
                    </div>

                    <ul className="posts-list">
                        {posts.map((post) => (
                            <li key={post.id} className="post-item">
                                <p>{post.content}</p>
                                <small>
                                    Posted by: {post.User ? post.User.username : 'Anonymous'} at {new Date(post.createdAt).toLocaleString()}
                                    <button
                                        className="reply-button"
                                        onClick={() => setReplyPost(post.id)}  // Log the post ID being replied to
                                    >
                                        Reply
                                    </button>
                                </small>

                                {/* Display replies */}
                                {post.Replies && post.Replies.length > 0 && (
                                    <ul className="replies-list">
                                        {post.Replies.map((reply) => (
                                            <li key={reply.id} className="reply-item">
                                                <p>{reply.content}</p>
                                                <small>
                                                    Replied by: {reply.User ? reply.User.username : 'Anonymous'} at {new Date(reply.createdAt).toLocaleString()}
                                                </small>
                                            </li>
                                        ))}
                                    </ul>
                                )}
                            </li>
                        ))}
                    </ul>

                    {replyPost && (
                        <form onSubmit={handleReplySubmit} className="new-reply-form">
                            <textarea
                                placeholder="Write your reply..."
                                value={newReply}
                                onChange={(e) => setNewReply(e.target.value)}
                                required
                            />
                            <button type="submit">Reply</button>
                        </form>
                    )}

                    <form onSubmit={handleSubmitPost} className="new-post-form">
                        <textarea
                            placeholder="Write your post..."
                            value={newPost}
                            onChange={(e) => setNewPost(e.target.value)}
                            required
                        />
                        <button type="submit">Post</button>
                    </form>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
