import React, { useEffect, useState } from 'react';

function TrendingTopics() {
    const [trendingTopics, setTrendingTopics] = useState([]);

    useEffect(() => {
        const fetchTrendingTopics = async () => {
            try {
                const response = await fetch('http://localhost:5000/api/topics/trending');
                const data = await response.json();

                if (Array.isArray(data)) {
                    setTrendingTopics(data); // Only set the data if it's an array
                } else {
                    setTrendingTopics([]); // Handle unexpected response structure
                }
            } catch (error) {
                console.error('Error fetching trending topics:', error);
            }
        };

        fetchTrendingTopics();
    }, []);

    if (!Array.isArray(trendingTopics) || trendingTopics.length === 0) {
        return <div className="trending-topics">No trending topics available.</div>;
    }

    return (
        <div className="trending-topics">
            <h3>Tendances</h3>
            <ul>
                {trendingTopics.map(topic => (
                    <li key={topic.id}>
                        <a href={`/topic/${topic.id}`}>
                            {topic.title} (Posts: {topic.postCount})
                        </a>
                        <p>Posted by: {topic.Posts.map(post => post.User.username).join(', ')}</p>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TrendingTopics;
