import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import '../css/ForumTopic.css';  // Importation du CSS

function ForumTopic() {
    const [topic, setTopic] = useState(null);
    const [posts, setPosts] = useState([]);
    const { id } = useParams();  // Récupère l'ID du topic depuis l'URL

    useEffect(() => {
        // Récupérer le topic et ses posts
        fetch(`http://localhost:5000/api/topics/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setTopic(data);
                setPosts(data.Posts);  // Afficher tous les posts associés au topic
            });
    }, [id]);

    return (
        <div className="forum-page">
            {topic && (
                <>
                    <h2>{topic.title}</h2>
                    <p>{topic.description}</p>

                    <ul className="posts-list">
                        {posts.map((post) => (
                            <li key={post.id}>
                                <p>{post.content}</p>
                                <small>Posted by: {post.User ? post.User.username : 'Anonymous'}</small>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
