import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import '../css/ForumTopic.css';  // Import CSS

const socket = io('http://localhost:5000');  // Connect to Socket.IO

function ForumTopic() {
    const { id } = useParams();  // Get the topic ID from the URL
    const [topic, setTopic] = useState(null);
    const [posts, setPosts] = useState([]);  // Initialize posts as an empty array
    const [newPost, setNewPost] = useState('');
    const [loading, setLoading] = useState(true);  // Add a loading state

    useEffect(() => {
        const fetchTopicData = async () => {
            try {
                const response = await fetch(`http://localhost:5000/api/topics/${id}`);
                if (!response.ok) {
                    throw new Error('Failed to fetch topic data');
                }
                const data = await response.json();
                setTopic(data);
                setPosts(data.Posts || []);  // Ensure posts are an array, even if empty
                setLoading(false);  // Stop loading when data is fetched
            } catch (error) {
                console.error('Error fetching topic:', error);
                setLoading(false);  // Stop loading even if there's an error
            }
        };

        fetchTopicData();

        // Listen for new posts via Socket.IO
        socket.on('newPost', (post) => {
            setPosts((prevPosts) => [post, ...prevPosts]);
        });

        return () => {
            socket.off('newPost');  // Clean up the socket event listener
        };
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        const token = localStorage.getItem('token');
        if (!token) {
            alert('You must be logged in to post.');
            return;
        }

        // Post the new comment to the server
        try {
            const response = await fetch(`http://localhost:5000/api/topics/${id}/posts`, {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                    'Authorization': `Bearer ${token}`
                },
                body: JSON.stringify({ content: newPost })
            });

            if (!response.ok) {
                throw new Error('Failed to post the message');
            }

            const createdPost = await response.json();

            // Emit the new post via Socket.IO for real-time update
            socket.emit('newPost', createdPost);

            // Clear the input and update UI immediately
            setNewPost('');  // Clear the input after submission
            setPosts((prevPosts) => [createdPost, ...prevPosts]);  // Add the post locally
        } catch (error) {
            alert(error.message);
        }
    };

    if (loading) {
        return <div className="loading">Loading...</div>;  // Show loading while fetching the topic
    }

    return (
        <div className="forum-topic">
            {topic && (
                <>
                    <div className="topic-header">
                        <h2>{topic.title}</h2>
                        <p>{topic.description}</p>
                    </div>

                    <ul className="posts-list">
                        {posts.length > 0 ? (
                            posts.map((post) => (
                                <li key={post.id} className="post-item">
                                    <p>{post.content}</p>
                                    <small>
                                        Posted by: {post.User ? post.User.username : 'Anonymous'} at {new Date(post.createdAt).toLocaleString()}
                                    </small>
                                </li>
                            ))
                        ) : (
                            <p>No posts yet. Be the first to comment!</p>
                        )}
                    </ul>

                    <form onSubmit={handleSubmit} className="new-post-form">
                        <textarea
                            placeholder="Write your post..."
                            value={newPost}
                            onChange={(e) => setNewPost(e.target.value)}
                            required
                        />
                        <button type="submit">Post</button>
                    </form>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
