import React, { useState, useEffect } from 'react';
import { io } from 'socket.io-client';
import { Link } from 'react-router-dom';
import '../css/TrendingTopics.css';  // Import CSS for styling

const socket = io('http://localhost:5000');

function TrendingTopics() {
    const [trendingTopics, setTrendingTopics] = useState([]);

    // Fetch trending topics from the backend
    const fetchTrendingTopics = async () => {
        try {
            const response = await fetch('http://localhost:5000/api/topics/trending');
            const data = await response.json();
            setTrendingTopics(data);
        } catch (error) {
            console.error('Error fetching trending topics:', error);
        }
    };

    useEffect(() => {
        fetchTrendingTopics();

        // Listen for updates on posts to refresh trending topics in real-time
        socket.on('newPost', () => {
            fetchTrendingTopics();
        });

        return () => {
            socket.off('newPost');
        };
    }, []);

    return (
        <div className="trending-topics">
            <h3>Tendances</h3>
            <ul>
                {trendingTopics.map((topic) => (
                    <li key={topic.id}>
                        <Link to={`/topic/${topic.id}`}>
                            <h4>{topic.title}</h4>
                            <p>{topic.description}</p>
                            <small>Posts: {topic.postCount}</small>
                        </Link>
                    </li>
                ))}
            </ul>
        </div>
    );
}

export default TrendingTopics;
