import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import '../css/ForumTopic.css';  // CSS pour styliser la page

const socket = io('http://localhost:5000');  // Connexion à Socket.IO

function ForumTopic() {
    const [topic, setTopic] = useState(null);  // Contient le topic actuel
    const [posts, setPosts] = useState([]);  // Contient les posts associés
    const { id } = useParams();  // Récupère l'ID du topic depuis l'URL

    useEffect(() => {
        // Vérifie que l'URL est correcte et que l'API est accessible
        fetch(`http://localhost:5000/api/topics/${id}`)
            .then((response) => {
                if (!response.ok) {
                    throw new Error('Network response was not ok');
                }
                return response.json();
            })
            .then((data) => {
                setTopic(data);
                setPosts(data.Posts);
            })
            .catch((error) => {
                console.error('Failed to fetch:', error);
            });
    }, [id]);


    const handleSubmit = (e) => {
        e.preventDefault();
        // Logic for submitting a new post (for now we focus on viewing posts)
    };

    return (
        <div className="forum-page">
            {topic && (
                <>
                    <h2>{topic.title}</h2>
                    <p>{topic.description}</p>

                    <ul className="posts-list">
                        {posts.map((post) => (
                            <li key={post.id}>
                                <p>{post.content}</p>
                                <small>Posted by: {post.User ? post.User.username : 'Anonymous'}</small>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
