import React from 'react';
import '../css/Home.css';  // Import your CSS file

// Import the components
import CreateTopicForm from '../components/CreateTopicForm';
import TopicsList from '../components/TopicsList';

function Home() {
    return (
        <div className="home-page">
            <h2 className="main-heading">Bienvenue sur l'exercice du server Web</h2>
            <p>Vous pouvez discuter avec des gens de la classe sur le même réseau.</p>

            <div className="home-content">
                {/* Left: Create Topic Form */}
                <div className="create-topic-section">
                    <h3>Créer un nouveau topic</h3>
                    <CreateTopicForm />
                </div>

                {/* Right: Topics List */}
                <div className="dashboard">
                    <h3>Derniers Topics</h3>
                    <TopicsList />
                </div>
            </div>
        </div>
    );
}

export default Home;
