import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Login.css';  // Adjust the path for your CSS

function Login() {
    const [email, setEmail] = useState('');  // Change username to email
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleLogin = async (e) => {
        e.preventDefault();

        const response = await fetch('http://localhost:5000/api/users/login', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ email: username, password })  // Send email instead of username
        });

        if (response.ok) {
            const data = await response.json();
            localStorage.setItem('token', data.token);
            navigate('/profile');
        } else {
            const data = await response.json();
            alert('Login failed: ' + data.message);
        }
    };

    return (
        <div className="form-wrapper">
            <div className="form-container">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <input
                        type="email"  // Change input type to email
                        placeholder="Email"
                        value={email}  // Use email state instead of username
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit">Login</button>
                </form>
            </div>
        </div>
    );
}

export default Login;
