import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import '../css/Signup.css';  // Ajuste le chemin pour pointer vers src/css

function Signup() {
    const [username, setUsername] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const navigate = useNavigate();

    const handleSignup = async (e) => {
        e.preventDefault();

        console.log({ username, email, password });  // Debugging: Log the values before sending

        const response = await fetch('http://localhost:5000/api/users/register', {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
            },
            body: JSON.stringify({ username, email, password })  // Ensure password is included
        });

        if (response.ok) {
            navigate('/login');
        } else {
            const data = await response.json();
            alert('Signup failed: ' + data.message);
        }
    };

    return (
        <div className="form-wrapper">
            <div className="form-container">
                <h2>Signup</h2>
                <form onSubmit={handleSignup}>
                    <input
                        type="text"
                        placeholder="Username"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        required
                    />
                    <input
                        type="email"
                        placeholder="Email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                    <button type="submit">Signup</button>
                </form>
            </div>
        </div>
    );
}

export default Signup;
