import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import '../css/ForumTopic.css';  // CSS pour styliser la page

const socket = io('http://localhost:5000');  // Connexion à Socket.IO

function ForumTopic() {
    const [topic, setTopic] = useState(null);  // Contient le topic actuel
    const [posts, setPosts] = useState([]);  // Contient les posts associés
    const { id } = useParams();  // Récupère l'ID du topic depuis l'URL

    useEffect(() => {
        // Récupérer le topic avec ses posts associés
        fetch(`http://localhost:5000/api/topics/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setTopic(data);  // Récupérer le topic
                setPosts(data.Posts);  // Récupérer les posts associés au topic
            });

        // Écouter les nouveaux topics via Socket.IO
        socket.on('newPost', (post) => {
            setPosts((prevPosts) => [post, ...prevPosts]);
        });

        // Nettoyer les événements Socket.IO à la déconnexion
        return () => {
            socket.off('newPost');
        };
    }, [id]);

    const handleSubmit = (e) => {
        e.preventDefault();
        // Logic for submitting a new post (for now we focus on viewing posts)
    };

    return (
        <div className="forum-page">
            {topic && (
                <>
                    <h2>{topic.title}</h2>
                    <p>{topic.description}</p>

                    <ul className="posts-list">
                        {posts.map((post) => (
                            <li key={post.id}>
                                <p>{post.content}</p>
                                <small>Posted by: {post.User ? post.User.username : 'Anonymous'}</small>
                            </li>
                        ))}
                    </ul>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
