import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';
import { io } from 'socket.io-client';
import '../css/ForumTopic.css';  // Ensure CSS for styling

const socket = io('http://localhost:5000');  // WebSocket connection to backend

function ForumTopic() {
    const [topic, setTopic] = useState(null);
    const [posts, setPosts] = useState([]);
    const [newPost, setNewPost] = useState('');  // New post content
    const { id } = useParams();  // Get topic ID from URL

    useEffect(() => {
        // Fetch topic and posts
        fetch(`http://localhost:5000/api/topics/${id}`)
            .then((response) => response.json())
            .then((data) => {
                setTopic(data);
                setPosts(data.Posts);  // Load posts associated with the topic
            });

        // Socket.IO: Listen for new posts
        socket.on('newPost', (post) => {
            setPosts((prevPosts) => [post, ...prevPosts]);
        });

        // Cleanup Socket.IO when component unmounts
        return () => {
            socket.off('newPost');
        };
    }, [id]);

    const handleSubmit = async (e) => {
        e.preventDefault();

        // Send the post to the server
        const response = await fetch(`http://localhost:5000/api/topics/${id}/posts`, {
            method: 'POST',
            headers: {
                'Content-Type': 'application/json',
                'Authorization': `Bearer ${localStorage.getItem('token')}`  // User token for auth
            },
            body: JSON.stringify({ content: newPost })
        });

        if (response.ok) {
            setNewPost('');  // Clear input after submission
        } else {
            const data = await response.json();
            alert('Failed to post: ' + data.message);
        }
    };

    return (
        <div className="forum-page">
            {topic && (
                <>
                    <h2>{topic.title}</h2>
                    <p>{topic.description}</p>

                    <ul className="posts-list">
                        {posts.map((post) => (
                            <li key={post.id} className="post-item">
                                <p>{post.content}</p>
                                <small>Posted by: {post.User ? post.User.username : 'Anonymous'}</small>
                            </li>
                        ))}
                    </ul>

                    {/* Post form */}
                    <form onSubmit={handleSubmit} className="new-post-form">
                        <textarea
                            placeholder="Write your post..."
                            value={newPost}
                            onChange={(e) => setNewPost(e.target.value)}
                            required
                        />
                        <button type="submit">Post</button>
                    </form>
                </>
            )}
        </div>
    );
}

export default ForumTopic;
